mapper.update:
update role
set create_time = #{createTime,jdbcType=TIMESTAMP},
  `name` = #{name,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, create_time, `name`, update_time
from role
where id = #{id,jdbcType=BIGINT}
